// FetchUsers.js
import React, { useState } from "react";
import axios from "axios";
import UsersTable from "./UsersTable"; // Import UsersTable

const FetchUsers = () => {
  const [users, setUsers] = useState([]);
  const [status, setStatus] = useState(false); // Boolean status

  const fetchUsers = async () => {
    setStatus(true); // Set loading status to true
    try {
      const response = await axios.get("http://localhost:5000/api/users");
      console.log("Fetched Users:", response.data);
      setUsers(response.data); // Set users to state
      setStatus(false); // Set status to false after data is fetched
    } catch (error) {
      console.error("Error fetching users:", error.response?.data || error.message);
      setStatus(false); // Set status to false on error
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Users List</h2>

      <button
        onClick={fetchUsers}
        className="mb-6 bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 transition-colors"
      >
        Fetch Users
      </button>

      {status ? (
        <p className="mb-4 text-blue-500">Loading...</p> // Display loading status when true
      ) : (
        users.length === 0 && (
          <p className="mb-4 text-red-500">No users to display. Click the button above to fetch users.</p>
        )
      )}

      {users && users.length > 0 && !status ? (


        <UsersTable users={users} /> // Only show the table if users are available and status is false
      ) : null}
    </div>
  );
};

export default FetchUsers;
