import React, { useState } from "react";
import axios from "axios";
import UsersTable from "./UsersTable"; // Import UsersTable

const FetchUsers = () => {
  const [users, setUsers] = useState([]);
  const [status, setStatus] = useState(false); // Boolean status (true for loading, false for completed)
  const [showUsers, setShowUsers] = useState(true); // State to toggle UsersTable visibility

  const fetchUsers = async () => {
    setStatus(true); // Set loading status to true
    try {
      const response = await axios.get("http://localhost:5000/api/users");
      console.log("Fetched Users:", response.data);
      setUsers(response.data.UserList); // Set users to state
      setStatus(false); // Set status to false after data is fetched
    } catch (error) {
      console.error("Error fetching users:", error.response?.data || error.message);
      setStatus(false); // Set status to false on error
    }
  };

  // Function to toggle visibility of UsersTable
  const toggleUsersVisibility = () => {
    setShowUsers((prevState) => !prevState); // Toggle visibility
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <button
        onClick={fetchUsers}
        className="mb-6 bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 transition-colors"
      >
        Fetch Users
      </button>

      {/* Show loading status when fetching */}
      {status && <p className="mb-4 text-blue-500">Loading...</p>}

      {/* Show error message when no users are available */}
      {!status && users.length === 0 && (
        <p className="mb-4 text-red-500">No users to display. Click the button above to fetch users.</p>
      )}

      {/* Conditional rendering of UsersTable */}
      {!status && users.length > 0 && showUsers && (
        <>
          <UsersTable users={users} />
          <button
            onClick={toggleUsersVisibility}
            className="mt-4 bg-red-500 text-white py-2 px-4 rounded-md hover:bg-red-600 transition-colors"
          >
            Hide Users
          </button>
        </>
      )}

      {/* Show "Show Users" button if table is hidden */}
      {!showUsers && users.length > 0 && (
        <button
          onClick={toggleUsersVisibility}
          className="mt-4 bg-green-500 text-white py-2 px-4 rounded-md hover:bg-green-600 transition-colors"
        >
          Show Users
        </button>
      )}
    </div>
  );
};

export default FetchUsers;
