import React, { useState } from "react";
import axios from "axios";

const FetchUsers = () => {
  const [users, setUsers] = useState([]);
  const [status, setStatus] = useState("");

  const fetchUsers = async () => {
    setStatus("Loading..."); // Set loading status
    try {
      const response = await axios.get("http://localhost:5000/api/users");
      console.log("Fetched Users:", response.data);
      setUsers(response.data); // Set users to state
      setStatus(""); // Clear status after data is fetched
    } catch (error) {
      console.error("Error fetching users:", error.response?.data || error.message);
      setStatus("Error fetching users.");
    }
  };

  console.log("Users array length:", users.length); // Debugging log

  return (
    <div className="max-w-4xl mx-auto p-4">
      <button
        onClick={fetchUsers}
        className="mb-6 bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 transition-colors"
      >
        Fetch Users
      </button>

      {status && <p className="mb-4 text-red-500">{status}</p>} {/* Display status */}

      {users.length > 0 ? (
        <table className="w-full border-collapse border border-gray-300">
          <thead>
            <tr className="bg-gray-100">
              <th className="border border-gray-300 px-4 py-2 text-left">First Name</th>
              <th className="border border-gray-300 px-4 py-2 text-left">Last Name</th>
              <th className="border border-gray-300 px-4 py-2 text-left">Email</th>
              <th className="border border-gray-300 px-4 py-2 text-left">Phone</th>
              <th className="border border-gray-300 px-4 py-2 text-left">Company</th>
              <th className="border border-gray-300 px-4 py-2 text-left">Job Title</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id} className="hover:bg-gray-50">
                <td className="border border-gray-300 px-4 py-2">{user.firstName}</td>
                <td className="border border-gray-300 px-4 py-2">{user.lastName}</td>
                <td className="border border-gray-300 px-4 py-2">{user.email}</td>
                <td className="border border-gray-300 px-4 py-2">{user.phone}</td>
                <td className="border border-gray-300 px-4 py-2">{user.company}</td>
                <td className="border border-gray-300 px-4 py-2">{user.jobTitle}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-gray-600">No users to display. Click the button above to fetch users.</p>
      )}
    </div>
  );
};

export default FetchUsers;
