import React, { useState } from "react";
import axios from "axios";

const UsersTable = ({ users, setUsers }) => {
  console.log('Rendering UsersTable');

  // Declare state hooks at the top
  const [editingField, setEditingField] = useState(null);
  const [updatedData, setUpdatedData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    company: '',
    jobTitle: '',
  });

  if (!Array.isArray(users) || users.length === 0) {
    return <p>No users available.</p>; // Fallback message if users array is empty or not an array
  }

  // Function to handle the update of a user
  const handleUpdate = async (userId) => {
    console.log("Updating user with ID:", userId);

    if (!userId) {
      console.error("User ID is undefined or invalid");
      return;
    }

    const fieldToUpdate = updatedData[editingField]; // Get the field that was edited

    if (!fieldToUpdate) {
      console.error("No field updated");
      return;
    }

    try {
      const response = await axios.put(
        `http://localhost:5000/api/users/update/${userId}`,
        { [editingField]: fieldToUpdate } // Only send the updated field
      );
      console.log("User updated:", response.data);

      // Update only the user that is being edited
      setUsers((prevUsers) =>
        prevUsers.map((user) =>
          user._id === userId ? { ...user, [editingField]: fieldToUpdate } : user
        )
      );

      // Reset the editing state
      setEditingField(null);
      setUpdatedData({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        company: '',
        jobTitle: '',
      });
    } catch (error) {
      console.error("Error updating user:", error.response?.data || error.message);
    }
  };

  // Function to handle the deletion of a user
  const handleDelete = async (userId) => {
    console.log("Deleting user with ID:", userId);

    try {
      const response = await axios.delete(
        `http://localhost:5000/api/users/delete/${userId}`
      );
      console.log("User deleted:", response.data);

      // Immediately update state to remove the deleted user
      setUsers((prevUsers) => prevUsers.filter((user) => user._id !== userId));
    } catch (error) {
      console.error("Error deleting user:", error.response?.data || error.message);
    }
  };

  // Handle field edit
  const handleFieldClick = (field, value) => {
    setEditingField(field);
    setUpdatedData((prevData) => ({
      ...prevData,
      [field]: value,
    }));
  };

  return (
    <div>
      {users.length === 0 ? (
        <p>No users available.</p>
      ) : (
        <table className="w-full border-collapse border border-gray-300">
          <thead>
            <tr className="bg-gray-100">
              <th className="border border-gray-300 px-4 py-2 text-left text-black">First Name</th>
              <th className="border border-gray-300 px-4 py-2 text-left text-black">Last Name</th>
              <th className="border border-gray-300 px-4 py-2 text-left text-black">Email</th>
              <th className="border border-gray-300 px-4 py-2 text-left text-black">Phone</th>
              <th className="border border-gray-300 px-4 py-2 text-left text-black">Company</th>
              <th className="border border-gray-300 px-4 py-2 text-left text-black">Job Title</th>
              <th className="border border-gray-300 px-4 py-2 text-left text-black">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id || `${user.firstName}-${user.lastName}`} className="hover:bg-gray-50">
                <td className="border border-gray-300 px-4 py-2 text-black">
                  {editingField === 'firstName' ? (
                    <input
                      type="text"
                      value={updatedData.firstName}
                      onChange={(e) =>
                        setUpdatedData({ ...updatedData, firstName: e.target.value })
                      }
                    />
                  ) : (
                    <span onClick={() => handleFieldClick('firstName', user.firstName)}>
                      {user.firstName || 'N/A'}
                    </span>
                  )}
                </td>
                <td className="border border-gray-300 px-4 py-2 text-black">
                  {editingField === 'lastName' ? (
                    <input
                      type="text"
                      value={updatedData.lastName}
                      onChange={(e) =>
                        setUpdatedData({ ...updatedData, lastName: e.target.value })
                      }
                    />
                  ) : (
                    <span onClick={() => handleFieldClick('lastName', user.lastName)}>
                      {user.lastName || 'N/A'}
                    </span>
                  )}
                </td>
                <td className="border border-gray-300 px-4 py-2 text-black">{user.email || 'N/A'}</td>
                <td className="border border-gray-300 px-4 py-2 text-black">{user.phone || 'N/A'}</td>
                <td className="border border-gray-300 px-4 py-2 text-black">{user.company || 'N/A'}</td>
                <td className="border border-gray-300 px-4 py-2 text-black">{user.jobTitle || 'N/A'}</td>
                <td className="border border-gray-300 px-4 py-2 text-black">
                  <button
                    onClick={() => handleUpdate(user._id)}
                    disabled={editingField === null} // Disable button if no edit is being made
                    className="mr-2 bg-blue-500 text-white py-1 px-3 rounded-md hover:bg-blue-600 transition-colors"
                  >
                    Update
                  </button>
                  <button
                    onClick={() => handleDelete(user._id)}
                    className="bg-red-500 text-white py-1 px-3 rounded-md hover:bg-red-600 transition-colors"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default UsersTable;
